import React, { useState } from "react";
import Card from "../UI/Card";
import Button from "../UI/Button";
import styles from "./UserForm.module.css";

const UserForm = (props) => {
  const [userInput, setUserInput] = useState({
    enteredName: "",
    enteredAge: "",
  });

  const nameChangeHandler = (event) => {
    setUserInput((prevInput) => {
      return { ...prevInput, enteredName: event.target.value };
    });
  };
  const ageChangeHandler = (event) => {
    setUserInput((prevInput) => {
      return { ...prevInput, enteredAge: event.target.value };
    });
  };
  const submitHandler = (event) => {
    event.preventDefault();
    const userData = {
      name: userInput.enteredName,
      age: userInput.enteredAge,
    };
    props.onSaveUserData(userData);
    setUserInput({
      enteredName: "",
      enteredAge: "",
    });
  };

  return (
    <Card className={styles.input}>
      <form onSubmit={submitHandler}>
        <div>
          <div>
            <label htmlFor="username">Name: </label>
            <input
              id="username"
              type="text"
              value={userInput.enteredName}
              onChange={nameChangeHandler}
            />
          </div>
          <div>
            <label>Age: </label>
            <input
              id="username"
              type="number"
              min="0"
              step="1"
              value={userInput.enteredAge}
              onChange={ageChangeHandler}
            />
          </div>
        </div>{" "}
        <div>
          <uBtton type="submit">Add User</uBtton>
        </div>
      </form>
    </Card>
  );
};

export default UserForm;
