import React, { useState } from "react";
import Card from "../UI/Card";
import Button from "../UI/Button";
import ErrorModal from "../UI/ErrorModal";
import styles from "./UserForm.module.css";

const UserForm = (props) => {
  const [error, setError] = useState();
  const [userInput, setUserInput] = useState({
    enteredName: "",
    enteredAge: "",
  });

  const submitHandler = (event) => {
    if (
      userInput.enteredName.trim().length === 0 ||
      userInput.enteredAge.trim().length === 0
    ) {
      setError({
        title: "Empty input",
        message: "Please enter a valid name and age (non-empty values).",
      });
      return;
    }
    if (+userInput.enteredAge < 1 || +userInput.enteredAge > 100) {
      setError({
        title: "Invalid age",
        message: "Please enter a reasonable age (1 ~ 100).",
      });
      return;
    }

    event.preventDefault();
    const userData = {
      name: userInput.enteredName,
      age: userInput.enteredAge,
    };
    props.onSaveUserData(userData);
    setUserInput({
      enteredName: "",
      enteredAge: "",
    });
  };

  const nameChangeHandler = (event) => {
    setUserInput((prevInput) => {
      return { ...prevInput, enteredName: event.target.value };
    });
  };
  const ageChangeHandler = (event) => {
    setUserInput((prevInput) => {
      return { ...prevInput, enteredAge: event.target.value };
    });
  };

  const errorHandler = () => {
    setError(null);
  };

  return (
    <div>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onCancelError={errorHandler}
        />
      )}
      <Card className={styles.input}>
        <form onSubmit={submitHandler}>
          <label htmlFor="username">Name</label>
          <input
            id="username"
            type="text"
            value={userInput.enteredName}
            onChange={nameChangeHandler}
          />

          <label>Age</label>
          <input
            id="username"
            type="number"
            step="1"
            value={userInput.enteredAge}
            onChange={ageChangeHandler}
          />

          <Button type="submit">Add User</Button>
        </form>
      </Card>
    </div>
  );
};

export default UserForm;
