import React, { useState } from "react";
import UserForm from "./UserForm";
//import Warning from "../Warning/Warning";

const NewUser = (props) => {
  const [isEmpty, setIsEmpty] = useState(false);
  const [isNotValid, setIsNotValid] = useState(false);

  const saveUserDataHandler = (enteredUserData) => {
    if (
      enteredUserData.age.trim().length === 0 ||
      enteredUserData.name.trim().length === 0
    ) {
      setIsEmpty(true);
      return;
    }
    if (+enteredUserData.age > 100 || +enteredUserData.age < 1) {
      setIsNotValid(true);
      return;
    }
    const userData = {
      ...enteredUserData,
      id: Math.random().toString(),
    };
    props.onAddUser(userData);
  };
  return (
    <div>
      <UserForm onSaveUserData={saveUserDataHandler} />
      {isEmpty && <div>input empty</div>}
      {isNotValid && <div>age error</div>}
    </div>
  );
};

export default NewUser;
